package com.szkk.data;import java.io.Serializable;import org.json.JSONObject;public class ApplicationObject implements Serializable{	public static final long serialVersionUID = 1L;	public int id;		public ApplicationObject(JSONObject jsonObj){		if(jsonObj == null){			return;		}		updateWithJSON(jsonObj);	}		public ApplicationObject(){			}   		public void updateWithJSON(JSONObject jsonObj){		if(jsonObj == null){			return;		}				if(jsonObj.has("id")){			id = jsonObj.optInt("id");		}	}	public int getId() {		return id;	}	public void setId(int id) {		this.id = id;	}	}