package com.szkk.data;import org.json.JSONObject;public class Notification extends ApplicationObject{	private static final long serialVersionUID = 1L;	private String title;	private String content;	private String date;	private String type;	private String receiver;	private String publisher;	private boolean isNew;		public Notification(JSONObject jsonObj){		if(jsonObj == null){			return;		}		updateWithJSON(jsonObj);	}	  	@Override	public void updateWithJSON(JSONObject jsonObj) {		super.updateWithJSON(jsonObj);		if(jsonObj == null){			return;		}		if(jsonObj.has("title")){			title = jsonObj.optString("title");		}		if(jsonObj.has("content")){			title = jsonObj.optString("content");		}		if(jsonObj.has("date")){			title = jsonObj.optString("date");		}		if(jsonObj.has("type")){			title = jsonObj.optString("type");		}		if(jsonObj.has("receiver")){			title = jsonObj.optString("receiver");		}		if(jsonObj.has("publisher")){			title = jsonObj.optString("publisher");		}		if(jsonObj.has("is_new")){			isNew = jsonObj.optBoolean("is_new");		}	}		public boolean isNew() {		return isNew;	}	public void setNew(boolean isNew) {		this.isNew = isNew;	}	public String getTitle() {		return title;	}	public void setTitle(String title) {		this.title = title;	}	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}	public String getDate() {		return date;	}	public void setDate(String date) {		this.date = date;	}	public String getType() {		return type;	}	public void setType(String type) {		this.type = type;	}	public String getReceiver() {		return receiver;	}	public void setReceiver(String receiver) {		this.receiver = receiver;	}	public String getPublisher() {		return publisher;	}	public void setPublisher(String publisher) {		this.publisher = publisher;	}	public static long getSerialversionuid() {		return serialVersionUID;	}}