package com.szkk.data;import java.util.ArrayList;import java.util.List;import org.json.JSONArray;import org.json.JSONObject;public class User extends ApplicationObject{	private String name;	private String account;	private String salt;	private int gender;	private String email;	private String birthday;	private String homeTown;	private String address;	private String tel;	private String homePage;	private String phone;	private String qqNumber;	private String occupation;	private String zip;	private List<KK> kkList;		public User(JSONObject jsonObj){		if(jsonObj == null){			return;		}		updateWithJSON(jsonObj);	}		public void updateWithJSON(JSONObject jsonObj){		super.updateWithJSON(jsonObj);		if(jsonObj == null){			return;		}				if(jsonObj.has("name")){			name = jsonObj.optString("name");		}		if(jsonObj.has("account")){			account = jsonObj.optString("account");		}		if(jsonObj.has("salt")){			salt = jsonObj.optString("salt");		}		if(jsonObj.has("gender")){			gender = jsonObj.optInt("gender");		}		if(jsonObj.has("email")){			email = jsonObj.optString("email");		}		if(jsonObj.has("birthday")){			birthday = jsonObj.optString("birthday");		}		if(jsonObj.has("homeTown")){			homeTown = jsonObj.optString("homeTown");		}		if(jsonObj.has("address")){			address = jsonObj.optString("address");		}		if(jsonObj.has("tel")){			tel = jsonObj.optString("tel");		}		if(jsonObj.has("homePage")){			homePage = jsonObj.optString("homePage");		}		if(jsonObj.has("phone")){			phone = jsonObj.optString("phone");		}		if(jsonObj.has("qqNumber")){			qqNumber = jsonObj.optString("qqNumber");		}		if(jsonObj.has("occupation")){			occupation = jsonObj.optString("occupation");		}		if(jsonObj.has("zip")){			zip = jsonObj.optString("zip");		}		if(jsonObj.has("archives")){          			JSONArray array = jsonObj.optJSONArray("archives");			if(array == null){				return;			}			kkList = null;			kkList = new ArrayList<KK>();			for(int i=0; i<array.length(); ++i){				KK kk = new KK(array.optJSONObject(i));				kkList.add(kk);			}		}	}	public String getName() {   		return name;	}	public void setName(String name) {		this.name = name;	}	public String getAccount() {		return account;	}	public void setAccount(String account) {		this.account = account;	}	public String getSalt() {		return salt;	}	public void setSalt(String salt) {		this.salt = salt;	}	public int getGender() {		return gender;	}	public void setGender(int gender) {		this.gender = gender;	}	public String getEmail() {		return email;	}	public void setEmail(String email) {		this.email = email;	}	public String getBirthday() {		return birthday;	}	public void setBirthday(String birthday) {		this.birthday = birthday;	}	public String getHomeTown() {		return homeTown;	}	public void setHomeTown(String homeTown) {		this.homeTown = homeTown;	}	public String getAddress() {		return address;	}	public void setAddress(String address) {		this.address = address;	}	public String getTel() {		return tel;	}	public void setTel(String tel) {		this.tel = tel;	}	public String getHomePage() {		return homePage;	}	public void setHomePage(String homePage) {		this.homePage = homePage;	}	public String getPhone() {		return phone;	}	public void setPhone(String phone) {		this.phone = phone;	}	public String getQqNumber() {		return qqNumber;	}	public void setQqNumber(String qqNumber) {		this.qqNumber = qqNumber;	}	public String getOccupation() {		return occupation;	}	public void setOccupation(String occupation) {		this.occupation = occupation;	}	public String getZip() {		return zip;	}	public void setZip(String zip) {		this.zip = zip;	}	public List<KK> getKkList() {		return kkList;	}	public void setKkList(List<KK> kkList) {		this.kkList = kkList;	}}