package com.szkk.setting;import com.szkk.www.R;import android.app.Dialog;import android.content.Context;import android.view.View;import android.widget.Button;public class ConsultDialog extends Dialog{	private Button closeBtn;	private Button deleteBtn;			public ConsultDialog(Context context, Object obj) {		super(context);		initView();	}	private void initView() {		closeBtn = (Button) findViewById(R.id.btn_close);		deleteBtn = (Button) findViewById(R.id.btn_delete);		closeBtn.setOnClickListener(listener);		deleteBtn.setOnClickListener(listener);	}		private View.OnClickListener listener = new View.OnClickListener() {				@Override		public void onClick(View v) {			switch(v.getId()){			case R.id.btn_close:				ConsultDialog.this.dismiss();				break;			case R.id.btn_delete:								break;			}		}	};}