package com.szkk.widget;import java.util.List;import android.content.Context;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import com.szkk.data.KK;public class ContentAdapter extends BaseAdapter{	private Context context;	private List<KK> kkList;		public ContentAdapter(Context context, List<KK> kkList){		this.context = context;		this.kkList = kkList;	}		@Override	public int getCount() {		return kkList.size();	}	@Override	public Object getItem(int position) {		return null;	}	@Override	public long getItemId(int position) {		return 0;	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		ItemContent item = null;		if(convertView == null){			item = new ItemContent(context);		}else{    			item = (ItemContent)convertView;		}  		item.initValue(kkList.get(position));		return item;	}}