package com.szkk.widget;import android.os.Bundle;import android.view.View;import android.view.ViewGroup;import android.webkit.WebSettings;import android.webkit.WebSettings.TextSize;import android.webkit.WebView;import android.widget.Button;import android.widget.TextView;import com.szkk.utils.ViewUtil;import com.szkk.www.R;import com.szkk.www.SZActivity;public class DiagnosisActivity extends SZActivity {	private WebView contentTview;	private Button backBtn;			@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.activity_diagnosis);		initView();		initValue();	}	private void initValue() {		String diagnosis = getIntent().getStringExtra("diagnosis");		contentTview.loadDataWithBaseURL(null, diagnosis, "text/html", "utf-8",				null);	}	private void initView() {		ViewGroup view = (ViewGroup) findViewById(R.id.main_layout);		ViewUtil.zoomView(view);		contentTview = (WebView) findViewById(R.id.wview);		backBtn = (Button) findViewById(R.id.btn_back);				WebSettings settings = contentTview.getSettings();		TextSize ts = TextSize.SMALLER;		settings.setTextSize(ts);				backBtn.setOnClickListener(new View.OnClickListener() {						@Override			public void onClick(View v) {				DiagnosisActivity.this.finish();				DiagnosisActivity.this.overridePendingTransition(R.anim.push_left_in, R.anim.push_left_out);			}		});	}}