package com.szkk.widget;import android.app.Dialog;import android.content.Context;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.TextView;import com.szkk.utils.ViewUtil;import com.szkk.www.R;public class DialogItemSearch extends Dialog{	private Context context;	private TextView contentTView;	private Button closeBtn;		public DialogItemSearch(Context context, int theme) {		super(context, theme);		this.context = context;		setContentView(R.layout.dialog_consult);		initView();	}		private void initView(){		ViewGroup view = (ViewGroup) findViewById(R.layout.dialog_consult);		ViewUtil.zoomView(view);				closeBtn = (Button) findViewById(R.id.btn_close);		contentTView = (TextView) findViewById(R.id.title_tview);		closeBtn.setOnClickListener(new View.OnClickListener() {						@Override			public void onClick(View v) {				DialogItemSearch.this.dismiss();			}		});	}}