package com.szkk.widget;import com.szkk.utils.ViewUtil;import com.szkk.www.MainActivity;import com.szkk.www.R;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.RelativeLayout;import android.widget.TextView;public class ItemConsultManager extends RelativeLayout{	private Context context;	private TextView tview_num;	private TextView tview_account;	private TextView tview_name;	private TextView tview_content;	private TextView tview_date;	private Button btn_preview;	private Button btn_delete;		public ItemConsultManager(Context context) {		super(context);		this.context = context;		initView();	}		private void initView() {  		ViewGroup view = (ViewGroup) LayoutInflater.from(context).inflate(R.layout.item_consult_manager, this);		ViewUtil.zoomView(view);				tview_num = (TextView) findViewById(R.id.num);		tview_account = (TextView) findViewById(R.id.kk_acount_tview);		tview_name = (TextView) findViewById(R.id.name_tview);		tview_content = (TextView) findViewById(R.id.content_tview);		tview_date = (TextView) findViewById(R.id.date_tview);		btn_preview = (Button) findViewById(R.id.btn_preview);		btn_delete = (Button) findViewById(R.id.btn_delete);				btn_preview.setOnClickListener(listener);		btn_delete.setOnClickListener(listener);	}		private View.OnClickListener listener = new View.OnClickListener() {				@Override		public void onClick(View v) {			switch(v.getId()){  			case R.id.btn_preview:				DialogConsult dialog = new DialogConsult(context, R.style.alert_dialog);				dialog.show();				break;			case R.id.btn_delete:								break;			}		}	};		public void initVaue(){			}}