package com.szkk.widget;import android.content.Context;import android.content.Intent;import android.util.Log;import android.view.LayoutInflater;import android.view.MotionEvent;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.RelativeLayout;import android.widget.TextView;import com.szkk.data.KK;import com.szkk.net.delegate.ArchivesTaskDelegate;import com.szkk.utils.ViewUtil;import com.szkk.www.MainActivity;import com.szkk.www.R;public class ItemContent extends RelativeLayout{	private Context context;	private TextView numTView;	private TextView nameTView;	private TextView genderTView;	private TextView ageTView;	private TextView analyseStatusTView;	private TextView handleStatusTView;	private TextView signTView;	private KK kk;		private Button delBtn;	private Button propertyBtn;	private Button previewBtn;		public ItemContent(Context context) {		super(context);		this.context = context;		initView();	}	public void initValue(KK kk){		this.kk = kk;		numTView.setText(kk.getId()+"");		nameTView.setText(kk.getName());		String gender = kk.getGender()==0?context.getResources().getString(R.string.female) :			context.getResources().getString(R.string.male);		genderTView.setText(gender);		ageTView.setText(kk.getAge());		analyseStatusTView.setText(kk.getAnalyseStatus());		handleStatusTView.setText(kk.getHandleStatus());		signTView.setText(kk.getSignName());	}		private void initView() {  		ViewGroup view = (ViewGroup) LayoutInflater.from(context).inflate(R.layout.content_item, this);		ViewUtil.zoomView(view);		numTView = (TextView) findViewById(R.id.tview_item_num);		nameTView = (TextView) findViewById(R.id.tview_item_name);		genderTView = (TextView) findViewById(R.id.tview_item_gender);		ageTView = (TextView) findViewById(R.id.tview_item_age);		analyseStatusTView = (TextView) findViewById(R.id.tview_item_anl_status);		handleStatusTView = (TextView) findViewById(R.id.tview_item_handler_status);		signTView = (TextView) findViewById(R.id.tview_item_signature);		RelativeLayout contentItem = (RelativeLayout) findViewById(R.id.content_item);				delBtn = (Button) findViewById(R.id.btn_delete);		propertyBtn = (Button) findViewById(R.id.btn_property);		previewBtn = (Button) findViewById(R.id.btn_preview);				delBtn.setOnClickListener(listener);		propertyBtn.setOnClickListener(listener);		previewBtn.setOnClickListener(listener);	}		private View.OnClickListener listener = new View.OnClickListener() {				@Override		public void onClick(View v) {			MainActivity act = (MainActivity)context;			switch(v.getId()){			case R.id.btn_delete:				act.onArchiveAction(ArchivesTaskDelegate.DEL, kk.getId());				break;			case R.id.btn_preview:				act.onArchiveAction(ArchivesTaskDelegate.DIA, kk.getId());				break;			case R.id.btn_property:				Intent intent = new Intent(context, ItemDetailActivity.class);				intent.putExtra("kk", kk);				act.startActivity(intent);				act.overridePendingTransition(R.anim.push_right_in, R.anim.push_right_out);			}		}	};}