package com.szkk.widget;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.RelativeLayout;import android.widget.TextView;import com.szkk.utils.ViewUtil;import com.szkk.www.R;public class ItemNotificationManager extends RelativeLayout{	private Context context;	private TextView tview_num;	private TextView tview_title;	private TextView tview_content;	private TextView tview_date;	private TextView tview_catalog;	private TextView tview_receiver;	private TextView tview_sender;	private Button btn_preview;	private Button btn_delete;		public ItemNotificationManager(Context context) {		super(context);		this.context = context;		initView();	}		private void initView() {  		ViewGroup view = (ViewGroup) LayoutInflater.from(context).inflate(R.layout.item_notification_manager, this);		ViewUtil.zoomView(view);  				tview_num = (TextView) findViewById(R.id.notification_num);		tview_title = (TextView) findViewById(R.id.notification_title_tview);		tview_content = (TextView) findViewById(R.id.notification_content_tview);		tview_date = (TextView) findViewById(R.id.notification_date_tview);		tview_catalog = (TextView) findViewById(R.id.notification_catalog_tview);		tview_receiver = (TextView) findViewById(R.id.notification_receiver_tview);		tview_sender = (TextView) findViewById(R.id.notification_sender_tview);		btn_preview = (Button) findViewById(R.id.btn_preview);		btn_delete = (Button) findViewById(R.id.btn_delete);				btn_preview.setOnClickListener(listener);		btn_delete.setOnClickListener(listener);	}		private View.OnClickListener listener = new View.OnClickListener() {				@Override		public void onClick(View v) {			switch(v.getId()){			case R.id.btn_preview:				DialogConsult dialog = new DialogConsult(context, R.style.alert_dialog);				dialog.show();				break;			case R.id.btn_delete:								break;			}		}	};		public void initVaue(){			}}