package com.szkk.widget;import com.szkk.www.R;import android.content.Context;import android.util.AttributeSet;import android.view.LayoutInflater;import android.widget.RelativeLayout;public class LoadingView extends RelativeLayout{	private Context context;		public LoadingView(Context context, AttributeSet attrs) {		super(context, attrs);		this.context = context;		initView();	}	private void initView() {		LayoutInflater.from(context).inflate(R.layout.loading_view, this);		this.setOnClickListener(null);	}	public void startLoading(){		this.setVisibility(VISIBLE);	}		public void stopLoading(){		this.setVisibility(GONE);	}}