package com.szkk.www;import com.szkk.utils.LocalUtil;import com.szkk.utils.ViewUtil;import com.szkk.widget.LoadingView;import android.app.Activity;import android.os.Bundle;import android.view.View;public class SZActivity extends Activity{	public  LoadingView loadingViewCommon;		@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		ViewUtil.shareActivity = this;		LocalUtil.shareActivity = this;		LocalUtil.instance = new LocalUtil();	}				@Override	protected void onResume() {		super.onResume();		ViewUtil.shareActivity = this;		LocalUtil.shareActivity = this;	}	@Override	public void onBackPressed() {		super.onBackPressed();		this.overridePendingTransition(R.anim.push_left_in, R.anim.push_left_out);	}		public void startLoading(){		loadingViewCommon.setVisibility(View.VISIBLE);	}		public void stopLoading(){		loadingViewCommon.setVisibility(View.GONE);	}	}